import React from "react";

export default function Grafico() {
  // Datos para el gr치fico
  const activos = 12;
  const eliminados = 5;

  // Calcular alturas relativas para las barras
  const max = Math.max(activos, eliminados);
  const alturaActivos = (activos / max) * 100;
  const alturaEliminados = (eliminados / max) * 100;

  return (
    <div style={{ width: "600px", margin: "40px auto" }}>
      <h1>Gr치fico de Libros</h1>
      
      <div style={{ display: "flex", alignItems: "flex-end", height: "300px", gap: "50px", padding: "20px 0" }}>
        
        {/* Barra de activos */}
        <div style={{ textAlign: "center" }}>
          <div
            style={{
              height: `${alturaActivos}%`,
              width: "80px",
              backgroundColor: "#4CAF50",
              marginBottom: "10px",
            }}
          />
          <div>Activos: {activos}</div>
        </div>
        
        {/* Barra de eliminados */}
        <div style={{ textAlign: "center" }}>
          <div
            style={{
              height: `${alturaEliminados}%`,
              width: "80px",
              backgroundColor: "#E53935",
              marginBottom: "10px",
            }}
          />
          <div>Eliminados: {eliminados}</div>
        </div>
      </div>
      
      <div style={{ marginTop: "20px", fontSize: "14px" }}>
        Total de libros: {activos + eliminados}
      </div>
    </div>
  );
}
